import React from "react";
import backgroundImage from "./result.png"; // Adjust path as necessary
import '../App.css'; // Or your main Tailwind CSS file

function Header() {
  return (
    <header className="min-h-screen">
      {/* Full page height background container */}
      <div className="bg-cover bg-no-repeat bg-center min-h-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
        <div className="container mx-auto px-6 md:px-12 lg:px-24 h-full">
          {/* Primary Navigation at the top */}
          <nav className="pt-4 flex justify-between items-center">
            {/* Logo Text */}
            <div>
              <a href="/" className="text-2xl font-bold text-white">AnswerMillion</a>
            </div>

            {/* Navigation Links */}
            <div className="flex items-center space-x-6">
              <a href="#about" className="text-lg font-medium hover:text-gray-300">About</a>
              <a href="#features" className="text-lg font-medium hover:text-gray-300">Features</a>
              {/* Additional primary navigation links can go here */}
            </div>
          </nav>

          {/* Additional header content aligned to the left and centered vertically */}
          <div className="flex-grow flex items-center">
            <div className="w-1/2 text-left pl-4 md:pl-0">
            <h1 className="text-4xl font-bold mb-2">AI Answering Service: Transforming Your Calls</h1>
<p className="text-xl md:text-3xl mb-4">Join AnswerMillion.com and step into the future of call management, where advanced AI technology ensures every communication is handled with precision and efficiency.</p>
              <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Join Waiting List</button>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
