import React, { useState } from 'react';

function EmailModal({ isOpen, onClose }) {
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/waiting-list`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      if (response.ok) {
        alert('Successfully added to the waiting list!');
        onClose(); // Close the modal on successful submission
      } else {
        alert('Failed to add to the waiting list.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting the form.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div className="mt-3 text-center">
          <h3 className="text-lg leading-6 font-medium text-gray-900">Join the Waiting List</h3>
          <div className="mt-2 px-7 py-3">
            <form onSubmit={handleSubmit}>
              <input
                type="email"
                placeholder="Your Email Address"
                className="px-4 py-2 w-full border rounded-md"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <button
                type="submit"
                className="bg-light-aqua text-white font-bold py-2 px-4 rounded-lg w-full mt-4"
              >
                Submit
              </button>
            </form>
          </div>
          <div className="items-center px-4 py-3">
            <button
              className="bg-white text-gray-700 font-bold py-2 px-4 rounded-md text-sm"
              onClick={onClose}
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EmailModal;
