import React from 'react';

function Features() {
  return (
    <div className="bg-background-dark text-white py-8">
      <div className="container mx-auto px-6 md:px-12 lg:px-24 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-8">
        {/* Feature 1 */}
        <div>
          <h5 className="text-aqua-text text-lg font-bold mb-2">AI-Powered Call Handling</h5>
          <p>Our advanced AI system efficiently handles incoming calls, ensuring every caller is greeted and directed appropriately.</p>
        </div>

        {/* Feature 2 */}
        <div>
          <h5 className="text-aqua-text text-lg font-bold mb-2">Smart Call Routing</h5>
          <p>Based on caller's requests, calls are smartly routed to the appropriate department or individual without delay.</p>
        </div>

        {/* Feature 3 */}
        <div>
          <h5 className="text-aqua-text text-lg font-bold mb-2">24/7 Availability</h5>
          <p>Your business never misses a call with our service operating round the clock, providing constant support for your clients.</p>
        </div>

        {/* Feature 4 */}
        <div>
          <h5 className="text-aqua-text text-lg font-bold mb-2">Customized Greetings</h5>
          <p>Personalize your caller's experience with customized greetings, making every interaction unique and engaging.</p>
        </div>
      </div>
    </div>
  );
}

export default Features;
